package com.xiaomi.analytics.internal.util;

import android.content.pm.Signature;
import android.os.Build;
import java.util.HashSet;

public class CertificateUtils {
    private static final String XIAOMI_PLATFORM_PUBLIC_KEY;

    static {
        if (Build.TYPE.equals("user")) {
            XIAOMI_PLATFORM_PUBLIC_KEY = "";
        } else {
            XIAOMI_PLATFORM_PUBLIC_KEY = "";
        }
    }

    public static int compareSignatures(Signature[] s1, Signature[] s2) {
        if (s1 == null) {
            if (s2 == null) {
                return 1;
            }
            return -1;
        } else if (s2 == null) {
            return -2;
        } else {
            HashSet<Signature> set1 = new HashSet();
            for (Signature sig : s1) {
                set1.add(sig);
            }
            HashSet<Signature> set2 = new HashSet();
            for (Signature sig2 : s2) {
                set2.add(sig2);
            }
            if (set1.equals(set2)) {
                return 0;
            }
            return -3;
        }
    }

    public static boolean isXiaomiPlatformCertificate(Signature[] sigs) {
        try {
            if (compareSignatures(sigs, new Signature[]{new Signature(XIAOMI_PLATFORM_PUBLIC_KEY)}) == 0) {
                return true;
            }
            return false;
        } catch (Exception e) {
            return false;
        }
    }
}
